select * from student 

and u_id > 0

		and s_name like "%"#{t.s_name}"%"


		and s_phone=#{t.s_phone}


		and s_qq=#{t.s_qq}


		and u_id=#{t.u_id}


		and s_jinbanshijian between #{t.stime} and #{t.etime}


		and s_huifang=#{t.s_huifang}


		and s_youxiao=#{t.s_youxiao}


		and s_jiaofei=#{t.s_jiaofei}



limit #{page},#{pageSize}

